import React, { useState } from "react";
import "./NewExpense.css";
import ExpenseForm from "../ExpenseForm";
function NewExpense(props) {
    const passExpenseData = expense => {
        setExpenses(preExpenses => {
          return [expense, ...preExpenses];
        })
      };

  const [showNewExpense, setShowNewExpense] = useState(false);
  const showNewExpenseHandler = () => {
    setShowNewExpense(true);
  };
  const cancelExpenseEditing =()=> {
    setShowNewExpense(false)
  }




  return (

    <div className="new-expense">
    {showNewExpense && <ExpenseForm onPassData ={passExpenseData} cancel={showNewExpense}/>}
      {!showNewExpense && (
        <button className='btn-exp' type="button" onClick={showNewExpenseHandler}>
          Add New Expense
        </button>
      )}


    </div>
  );
}
export default NewExpense;
